// function AddButtons(props) {

//     var watchedStyle = {opacity:'1'};
//     if ( props.isWatched )
//     {
//         watchedStyle = {opacity:'0.5'};
//     }

//     var wantedStyle = {opacity:'1'};
//     if ( props.isWatched )
//     {
//         watchedStyle = {opacity:'0.5'};
//     }
