import React, { Component } from "react";
import "./style.css";
import Loginpic from "./login.png";

function ToSign(props) {
    return (
        <button type="button" onClick={props.onClick} className="logbutton" id="not">Sign Up</button> 
    )
}

function ToLog(props) {
    return (
        <button type="button" onClick={props.onClick} className="logbutton" id="not">Log In</button>
    )
}

function ToHide(props) {
    return (
        <button class="closelog" onClick={props.onClick}><i class="fas fa-times"></i></button> 
    )
}

class Loginbox extends Component {


    


render () {
        console.log(this.props);
        let status = "";
        let login = (
            <div className="log-container">
                <div className="box">
                    <img src={Loginpic}></img>
                    <ToHide onClick={this.props.handleOnHideLog}/>
                <div className="formbox">
                    <h4>Username *</h4>
                    <input type="text" className="username" name="username"></input>
                    <br></br>
                    <h4>Password *</h4>
                    <input type="text" className="username" name="password"></input>
                    <br></br>
                    <button class="logingo"><span>Log In</span></button>
                    <hr></hr>
                    <ToSign onClick={this.props.handleOnSign} />
                 </div>
                </div>
            </div>
            
        );
        let signup = (
            <div className="log-container">
                <div className="box">
                    <h2>SIGN UP</h2>
                    <ToHide onClick={this.props.handleOnHideLog}/>
                    <div className="formbox">
                    
                    <h4>Username *</h4>
                    <input type="text" className="username" name="username"></input>
                    <br></br>
                    <h4>Password *</h4>
                    <input type="text" className="username" name="password"></input>
                    <br></br>
                    <button class="logingo"><span>Log In</span></button>
                        <hr></hr>
                        <ToLog onClick={this.props.handleOnLogIn} />
                    </div>
                </div>
            </div>
            
        );

        
        if (! this.props.isLog) {
            status = null;
        } else if (this.props.isLog && !this.props.isSignUp) {
            status = login;
        } else if (this.props.isLog && this.props.isSignUp) {
            status = signup;
        }
        return (
            <div>
                {status}
            </div>
        )

        if (! this.props.isSignUp) {
            status = login;
        } else {
            status = signup;
        }
        return (
            <div>
                {status}
            </div>
        )

        
 
      
    
  }
}


  export default Loginbox;
