import React, { Component } from 'react';
import "./style.css";
import Star from './../result-cards/star.png';
import StarRatingComponent from 'react-star-rating-component';
import { realpathSync } from 'fs';

function StarRate(props) {

    return (
        <div id="reviewStars-input">
            <input id="star-4" type="radio" name="reviewStars"/>
            <label title="great" for="star-4"></label>

            <input id="star-3" type="radio" name="reviewStars"/>
            <label title="good" for="star-3"></label>

            <input id="star-2" type="radio" name="reviewStars"/>
            <label title="neutral" for="star-2"></label>

            <input id="star-1" type="radio" name="reviewStars"/>
            <label title="poor" for="star-1"></label>

            <input id="star-0" type="radio" name="reviewStars"/>
            <label title="bad" for="star-0"></label>
        </div>
    )
}

function StarQual(props) {
    return (
        <div className="rating-label">
            <h3>Quality: </h3>
            <StarRatingComponent
                name="qualityRating"
                starCount={5}
                value={props.starRating}
                onStarClick={props.onStarClick}
                starColor={'#cc3333'}
                emptyStarColor={'#bbb'}
                renderStarIcon={() => <span><i classNAme="fas fa-star"></i></span>}
            />
        </div>
    )
}

function StarEnt(props) {
    return (
        <div className="rating-label">
            <h3>Entertainment: </h3>
            <StarRatingComponent
                name="entertainmentRating"
                starCount={5}
                value={props.starRating}
                onStarClick={props.onStarClick}
                starColor={'#cc3333'}
                emptyStarColor={'#bbb'}
                renderStarIcon={() => <span><i classNAme="fas fa-star"></i></span>}
            />
        </div>
    )
}

function StarScare(props) {
    return (
        <div className="rating-label">
            <h3>Scariness: </h3>
            <StarRatingComponent
                name="scareRating"
                starCount={5}
                value={props.starRating}
                onStarClick={props.onStarClick}
                starColor={'#cc3333'}
                emptyStarColor={'#bbb'}
                renderStarIcon={() => <span><i classNAme="fas fa-star"></i></span>}
            />
        </div>
    )
}

function AddButtons(props) {

    var watchedStyle = {opacity:'1'};
    if ( props.isWatched )
    {
        watchedStyle = {opacity:'0.5'};
    }

    var wantedStyle = {opacity:'1'};
    if ( props.isWatched )
    {
        watchedStyle = {opacity:'0.5'};
    }
}

class MoviePage extends Component {

    userQ = 1;
    userE = 1;
    userS = 1;
    
    handleOnStrClick = (nextValue, prevValue, name) => {
        switch( name )
        {
            case 'qualityRating':
            this.userQ = nextValue;
            break;

            case 'entertainmentRating':
            this.userE = nextValue;
            break;

            case 'scareRating':
            this.userS = nextValue;
            break;
        }
        this.updateReview();
    }

    updateReview = () => {
        this.props.onUpdateReview(this.userQ, this.userE, this.userS);
    }

    render() {
        var {movie} = this.props;
        
        if ( movie.title === undefined )
        {
            return (
                <React.Fragment>
                    <div ClassName="all"></div>
                </React.Fragment>
            );
        }

        var {title, overview, release_date, poster_path, runtime, director} = movie;
        var year = release_date.substring(0,4);
        var posterImg = 'http://image.tmdb.org/t/p/w342' + poster_path;
        var {original_language} = movie;

        this.userQ = movieuserQ;
        this.userE = movie.userE;
        this.userS= movie.userS;

        var language;
        switch ( original_language )
        {
            case "en":
                language = "English";
                break;
            case "es":
                language = "Spanish";
                break;
            case "it":
                language = "Italian";
                break;
            case "ko":
                language = "Korean";
                break;
            case "ja":
                language = "Japanese";
                break;
            case "de":
                language = "German";
                break;
            case "fr":
                language = "French";
                break;
            default:
                language = "English";
                break;
        }
        var { recommendations } = movie;
        var { isWatched, isWanted } = this.props;

        return (
            <React.Fragment>
                <div className="all">
                    <div className="page">
                        <img className="page-poster" src={posterImg}></img>
                        <div className="movie-title">
                            <h1>
                                <span className="movie-name">{title}</span>
                            </h1>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }

}

export default MoviePage;
