import React, { Component } from "react";
import "./style.css";
import Loginpic from "./login.png";
import Signuppic from "./signup.png";
import X from "./close.png";

function ToSign(props) {
    return (
        <button type="button" onClick={props.onClick} className="logbutton">Sign Up</button> 
    )
}

function ToLog(props) {
    return (
        <button type="button" onClick={props.onClick} className="logbutton">Log In</button>
    )
}

function ToHide(props) {
    return (
        <button class="closelog" onClick={props.onClick}><img src={X}></img></button> 
    )
}

class Loginbox extends Component {


    


render () {
        console.log(this.props);
        let status = "";
        let login = (
            <div className="log-container">
                <div className="box">
                    <ToHide onClick={this.props.handleOnHideLog}/>
                    <br></br>
                    <img className="logtitle" src={Loginpic}></img>
                <div className="formbox">
                    <h4>Username *</h4>
                    <input type="text" className="username" name="username"></input>
                    <br></br>
                    <h4>Password *</h4>
                    <input type="text" className="username" name="password"></input>
                    <br></br>
                    <div className="invalid">Invalid user name</div>
                    <button className="logingo"><span>Log In</span></button>
                    <hr></hr>
                    <h5>New to Horror Scope?</h5>
                    <ToSign onClick={this.props.handleOnSign} />
                 </div>
                </div>
            </div>
            
        );
        let signup = (
            <div className="log-container">
                <div className="box">
                    <ToHide onClick={this.props.handleOnHideLog}/>
                    <img className="logtitle" src={Signuppic}></img>
                    
                    <div className="formbox">
                    
                    <h4>Username *</h4>
                    <input type="text" className="username" name="username"></input>
                    <br></br>
                    <h4>Password *</h4>
                    <input type="text" className="username" name="password"></input>
                    <br></br>
                    <button class="logingo"><span>Sign Up</span></button>
                        <hr></hr>
                        <h5>Already a member?</h5>
                        <ToLog onClick={this.props.handleOnLogIn} />
                    </div>
                </div>
            </div>
            
        );

        
        if (! this.props.isLog) {
            status = null;
        } else if (this.props.isLog && !this.props.isSignUp) {
            status = login;
        } else if (this.props.isLog && this.props.isSignUp) {
            status = signup;
        }
        return (
            <div>
                {status}
            </div>
        )

        if (! this.props.isSignUp) {
            status = login;
        } else {
            status = signup;
        }
        return (
            <div>
                {status}
            </div>
        )

        
 
      
    
  }
}


  export default Loginbox;
